drop database test;

create database test character set utf8 collate utf8_bin;
grant all privileges on test.* to root@localhost identified by 'root';
USE test;

create table tasks(
	id int not null auto_increment, 
	name varchar(255) not null, 
	description text, 
	priority varchar(20), 
	status varchar(20),
	primary key(id)
);

второй - подбор рекламных роликов, а третья - статистика для директора. Собственно, к ней мы и добрались', 'Нормальный', 'Действует');
select * from tasks;